import csvimport osfrom datetime import date, datetimefrom typing import Listfrom bisect import bisect, bisect_left, insort_leftfrom werkzeug.security import generate_password_hashfrom movie_web_app.a_adapters.repository import AbstractRepository, RepositoryExceptionfrom movie_web_app.domain.model import Director, Genre, Actor, Movie, Review, User, WatchListfrom movie_web_app.datafilereaders.movie_file_csv_reader import MovieFileCSVReaderfrom movie_web_app.activitysimulations.watchingsimulation import MovieWatchingSimulationclass PeopleRepository(AbstractRepository):    def __init__(self, *args):        self._people: List[Person] = list()        for person in args:            self._people.append(person)    def __iter__(self):        self._current = 0        return self    def __next__(self):        if self._current >= len(self._people):            raise StopIteration        else:            self._current += 1            return self._people[self._current-1]    def get_person(self, id: int):        return next((person for person in self._people if person.id_number == id), None)